import React from 'react'


class TaskForm extends React.Component {

    state = {
        input: "",
        category: ""
    }

    handleChange = (e) => {

        this.setState({[e.target.name]: e.target.value})
        console.log(this.state)
    }

    submitHandler = (e) => {
        e.preventDefault()
        console.log("submitted")
    }

    render() {
        return (
            <form onSubmit={this.submitHandler} >
                <input type="text" placeholder="New Task Details" onChange={this.handleChange} ></input>
                <select>
                    <option>Code</option>
                    <option>Food</option>
                    <option>Money</option>
                    <option>Misc</option>
                </select>
                <input type="submit" value="Add Task"></input>
            </form>
        )
    }
}


export default TaskForm
