import React from 'react'


class TaskForm extends React.Component {

    state = {
        input: ""
    }

    handleChange = (e) => {
        this.setState({input: e.target.value})
        console.log(this.state.input)
    }

    render() {
        return (
            <form>
                <input type="text" placeholder="New Task Details" onChange={this.handleChange} ></input>
                <select>
                    <option>Code</option>
                    <option>Food</option>
                    <option>Money</option>
                    <option>Misc</option>
                </select>
                <input type="submit" value="Add Task"></input>
            </form>
        )
    }
}


export default TaskForm
