import React from "react";
import Task from "../Components/Task";

class TaskContainer extends React.Component {
  tasks = () => {
    return this.props.tasks.map((task) => <Task task={task} />);
  };

  filterTasks = () => {

    let filteredTasks =  this.props.tasks.filter((task) => task.category === this.props.category )
    filteredTasks.map((task)=> <Task task={task} />)

  }


  render() {
    return (
        <div className="tasks">
            {this.filterTasks()}
        </div>
        )
  }
}

export default TaskContainer;
